package test.ch04;

public class While_EX {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 1;
		// int sum = 0;

		// 1~100까지 합계구하기

		/*
		 * while(i <= 100) { sum += i; i++; } System.out.println(sum);
		 */

		do {
			i++;
			System.out.println(i);
		} while (i <= 10);

	}

}
